import React from "react"
import {Formik} from "formik"
import * as Yup from "yup"
import  {HashRouter as Router,Route,Switch,Link}  from "react-router-dom"
import * as ReactBootStrap from 'react-bootstrap';





const Validation = () => (
    <Formik 
     initialValues = {{email: "", password: ""}}
     onSubmit = {(values, { setSubmitting}) => {
         setTimeout(() => {
             console.log("Logged in", values)
         }, 500)
     }}

     validationSchema = {Yup.object().shape({
        email: Yup.string()
            .email()
            .required("Required"),
        password: Yup.string()
            .required("No password provided")
            .min(8,"Password has to be at least 8 characters.")
     })


    }
    >
        {props => {
            const {
                values,
                touched,
                errors,
                isSubmitting,
                handleChange,
                handleBlur,
                handleSubmit
            } = props

            const itemstyle = {
                width: 90,
                height: 160,
                border: "1,dashed",
                display: "table-cell",
                verticalAlign: "middle",
                textAlign: "center"
            }
            return (

                    <div>
                        <ReactBootStrap.Navbar bg="dark" variant="dark" style={itemstyle}>
                            <ReactBootStrap.Navbar.Brand href="#home">
                            <img
                                alt=""
                                src="/logo.png"
                                width="100"
                                height="100"
                                style={{display: "flex",
                                justifyContent: "center",
                                alignitems: "center"}}

                            />
                            Moyyn
                            </ReactBootStrap.Navbar.Brand>
                        </ReactBootStrap.Navbar>

                        <form autoComplete = "off" onSubmit = {handleSubmit}>
                            <h2 style={{fontSize: 22}}>Login to view our user pool</h2>
                            <label htmlFor="email">Email:</label>
                            <input type="text" value={values.email} name="email" onChange={handleChange} onBlur={handleBlur} placeholder="Enter your email" className={errors.email && touched.email && "error"}/>
                            {errors.email && touched.email && (<div className="input-feedback">{errors.email}</div>)}
                            <label htmlFor="email">Password:</label>
                            <input type="password" value={values.password} name="password" onChange={handleChange} onBlur={handleBlur} placeholder="Enter your password" className={errors.password && touched.password && "error"}/>
                            {errors.password && touched.password && (<div className="input-feedback">{errors.password}</div>)}
                            <Link to="/users"><button type="submit" disabled={isSubmitting}>Login</button> </Link>



                        </form>


                    </div>


            )
        }}
    </Formik>
)


export default Validation
